package com.wilson.hbase;import org.apache.hadoop.hbase.client.Put;import java.util.List;/** * HBase 服务接口类 * Created by babylon on 2016/12/4. */public interface HBaseService {    /**     * 批量写入数据     * @param tableName  表名称     * @param puts         Put 类型的列表     * @param waiting  是否等待线程执行完成  true 可以及时看到结果, false 让线程继续执行，并跳出此方法返回调用方主程序     * @return     */    void batchPut(final String tableName, final List<Put> puts, boolean waiting);}