package com.wilson.util;import java.text.ParseException;/** * 日期时间工具类 * @author Administrator * */public class CalculateUtils {    /**     * function	  传入两个字符串，转化成Long，然后求出差值     * param 连接时间,时间戳毫秒；关闭时间,时间戳毫秒；     * return String,连接时长,单位是秒     */    public static  Integer getSubstractionSeconds(String connectTime,String closeTime) throws ParseException {        Long minus=0L;        if((Long.parseLong(closeTime))-(Long.parseLong(connectTime))>=0){            minus = (Long.parseLong(closeTime) - (Long.parseLong(connectTime)))/1000;        }else{            minus = (Long.parseLong(connectTime) - (Long.parseLong(closeTime)))/1000;        }        return minus.intValue();    }    /**     * function	  传入两个字符串，转化成int，然后求出和     * return String,连接时长,单位是秒     */    public static  Integer getAddSeconds(String a1,String a2) throws ParseException {        return Integer.parseInt(a1)+Integer.parseInt(a2);    }    /**     * function	  传入两个字符串，转化成Long，然后返回大的值     * return long值比较大的那个字符串     */    public static  String getBiggerNum(String a1,String a2) throws ParseException {        if(Long.parseLong(a1)>=Long.parseLong(a2)){return a1;}        else{return a2;}    }    /**     * function	  传入两个字符串，转化成Long，然后返回大的值     * return long值比较大的那个字符串     */    public static  String getSmallerNum(String a1,String a2) throws ParseException {        if(Long.parseLong(a1)>=Long.parseLong(a2)){return a2;}        else{return a1;}    }}